class Solution(object):
    def threeSum(self, nums):
        """
        firstly sorted the array, once fixed the first term, the other two can be found via binary search.
        runtime: 2492 ms beats 6.4%
        memory: 18.7 MB beats 11.01%
        """
        res=[]
        nums = sorted(nums)
        for i in range(len(nums)-2):
            if nums[i]>0: # the array is sorted, if nums[i] >0, then the sum must >0
                return res
            if i > 0 and nums[i] == nums[i - 1]: # avoid duplicate combinations
                continue
            start = i+1
            end = len(nums)-1
            remain = 0-nums[i]
            while start<end:
                tmpsum = nums[start]+nums[end]
                if tmpsum>remain:
                    end=end-1
                elif tmpsum<remain:
                    start=start+1
                else:
                    tmp_res = sorted([nums[i],nums[start],nums[end]])
                    if tmp_res not in res:
                        res.append(tmp_res)
                    start=start+1
                    end = end-1
        return res

